import React, { useState } from "react";

function PrayerForm({ onPrayerAdded, onCancel }) {
  const [formData, setFormData] = useState({
    city: "",
    country: "",
    fajr: "",
    dhuhr: "",
    asr: "",
    maghrib: "",
    isha: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validateForm = () => {
    // Check if all fields are filled
    for (const key in formData) {
      if (!formData[key]) {
        setError(`Please fill in the ${key} field`);
        return false;
      }
    }

    // Basic time format validation (HH:MM)
    const timeFields = ["fajr", "dhuhr", "asr", "maghrib", "isha"];
    const timeRegex = /^([01]\d|2[0-3]):([0-5]\d)$/;
    
    for (const field of timeFields) {
      if (!timeRegex.test(formData[field])) {
        setError(`Please enter a valid time for ${field} (HH:MM format)`);
        return false;
      }
    }

    return true;
  };

  // In PrayerForm.js
const handleSubmit = async (e) => {
  e.preventDefault();
  setSubmitting(true);
  
  try {
    const response = await fetch('http://localhost:5000/api/prayers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });
    
    if (!response.ok) {
      throw new Error('Failed to add prayer times');
    }
    
    await response.json(); // Wait for the response
    
    // Only call onPrayerAdded after successful submission
    onPrayerAdded();
  } catch (error) {
    setError(error.message);
  } finally {
    setSubmitting(false);
  }
};
  return (
    <div className="prayer-form-container">
      <h2>Add Prayer Times</h2>
      
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">Prayer times added successfully!</div>}
      
      <form onSubmit={handleSubmit} className="prayer-form">
        <div className="form-group">
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            name="city"
            value={formData.city}
            onChange={handleChange}
            placeholder="Enter city name"
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="country">Country</label>
          <input
            type="text"
            id="country"
            name="country"
            value={formData.country}
            onChange={handleChange}
            placeholder="Enter country name"
            required
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="fajr">Fajr</label>
            <input
              type="text"
              id="fajr"
              name="fajr"
              value={formData.fajr}
              onChange={handleChange}
              placeholder="HH:MM"
              pattern="^([01]\d|2[0-3]):([0-5]\d)$"
              title="Enter time in HH:MM format"
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="dhuhr">Dhuhr</label>
            <input
              type="text"
              id="dhuhr"
              name="dhuhr"
              value={formData.dhuhr}
              onChange={handleChange}
              placeholder="HH:MM"
              pattern="^([01]\d|2[0-3]):([0-5]\d)$"
              title="Enter time in HH:MM format"
              required
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="asr">Asr</label>
            <input
              type="text"
              id="asr"
              name="asr"
              value={formData.asr}
              onChange={handleChange}
              placeholder="HH:MM"
              pattern="^([01]\d|2[0-3]):([0-5]\d)$"
              title="Enter time in HH:MM format"
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="maghrib">Maghrib (Iftar)</label>
            <input
              type="text"
              id="maghrib"
              name="maghrib"
              value={formData.maghrib}
              onChange={handleChange}
              placeholder="HH:MM"
              pattern="^([01]\d|2[0-3]):([0-5]\d)$"
              title="Enter time in HH:MM format"
              required
            />
          </div>
        </div>
        
        <div className="form-group">
          <label htmlFor="isha">Isha</label>
          <input
            type="text"
            id="isha"
            name="isha"
            value={formData.isha}
            onChange={handleChange}
            placeholder="HH:MM"
            pattern="^([01]\d|2[0-3]):([0-5]\d)$"
            title="Enter time in HH:MM format"
            required
          />
        </div>
        
        <div className="form-actions">
          <button 
            type="submit" 
            className="submit-btn" 
            disabled={isSubmitting}
          >
            {isSubmitting ? "Submitting..." : "Add Prayer Times"}
          </button>
          
          {onCancel && (
            <button 
              type="button" 
              className="cancel-btn" 
              onClick={onCancel}
              disabled={isSubmitting}
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default PrayerForm;
