import React, { useState } from "react";

function PrayerForm({ onPrayerAdded, onCancel }) {
  const [formData, setFormData] = useState({
    city: "",
    country: "",
    fajr: "",
    dhuhr: "",
    asr: "",
    maghrib: "",
    isha: ""
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const requiredFields = ['city', 'country', 'fajr', 'dhuhr', 'asr', 'maghrib', 'isha'];
    const missing = requiredFields.filter(field => !formData[field].trim());
    
    if (missing.length > 0) {
      setError(`Please fill in all required fields: ${missing.join(', ')}`);
      return false;
    }
    
    // Validate time format (HH:MM)
    const timeFields = ['fajr', 'dhuhr', 'asr', 'maghrib', 'isha'];
    const timeRegex = /^([01]?[0-9]|2[0-3]):[0-5][0-9]$/;
    
    const invalidTimes = timeFields.filter(field => !timeRegex.test(formData[field]));
    if (invalidTimes.length > 0) {
      setError(`Invalid time format for: ${invalidTimes.join(', ')}. Use HH:MM format.`);
      return false;
    }
    
    setError(null);
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    setSuccess(false);
    
    fetch("http://localhost:5000/api/prayers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(formData)
    })
      .then(res => {
        if (!res.ok) {
          throw new Error("Failed to save prayer times");
        }
        return res.json();
      })
      .catch(err => {
        console.error("API Error:", err);
        setError(err.message);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className="prayer-form">
      <h2>Add New Prayer Times</h2>
      
      {error && <div className="form-error">{error}</div>}
      {success && <div className="form-success">Prayer times added successfully!</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="city">City</label>
            <input
              type="text"
              id="city"
              name="city"
              value={formData.city}
              onChange={handleChange}
              placeholder="e.g. Mecca"
              disabled={loading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="country">Country</label>
            <input
              type="text"
              id="country"
              name="country"
              value={formData.country}
              onChange={handleChange}
              placeholder="e.g. Saudi Arabia"
              disabled={loading}
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="fajr">Fajr</label>
            <input
              type="text"
              id="fajr"
              name="fajr"
              value={formData.fajr}
              onChange={handleChange}
              placeholder="HH:MM"
              disabled={loading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="dhuhr">Dhuhr</label>
            <input
              type="text"
              id="dhuhr"
              name="dhuhr"
              value={formData.dhuhr}
              onChange={handleChange}
              placeholder="HH:MM"
              disabled={loading}
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="asr">Asr</label>
            <input
              type="text"
              id="asr"
              name="asr"
              value={formData.asr}
              onChange={handleChange}
              placeholder="HH:MM"
              disabled={loading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="maghrib">Maghrib</label>
            <input
              type="text"
              id="maghrib"
              name="maghrib"
              value={formData.maghrib}
              onChange={handleChange}
              placeholder="HH:MM"
              disabled={loading}
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="isha">Isha</label>
            <input
              type="text"
              id="isha"
              name="isha"
              value={formData.isha}
              onChange={handleChange}
              placeholder="HH:MM"
              disabled={loading}
            />
          </div>
        </div>
        
        <div className="form-actions">
          <button 
            type="button" 
            onClick={onCancel} 
            disabled={loading}
            className="cancel-btn"
          >
            Cancel
          </button>
          <button 
            type="submit" 
            disabled={loading}
            className="submit-btn"
          >
            {loading ? "Saving..." : "Save Prayer Times"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PrayerForm;
