import React, { useEffect, useState } from "react";
import "./App.css"; // We'll create this file for styling

function App() {
  const [prayers, setPrayers] = useState(null);
  const [timeLeft, setTimeLeft] = useState("");
  const [activeTime, setActiveTime] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState("light");
  const [debugInfo, setDebugInfo] = useState(null);

  useEffect(() => {
    fetchPrayerTimes();

    // Apply theme class to body
    document.body.className = theme;

    return () => {
      document.body.className = "";
    };
  }, [theme]);

  const fetchPrayerTimes = () => {
    setLoading(true);
    setError(null);
    setDebugInfo(null);

    // Create hardcoded data as fallback
    const hardcodedData = {
      city: "Mecca",
      country: "Saudi Arabia",
      prayers: {
        Fajr: "04:35",
        Sunrise: "06:00",
        Dhuhr: "12:15",
        Asr: "15:30",
        Maghrib: "18:32",
        Isha: "19:45"
      }
    };

    fetch("http://localhost:5000/api/prayers")
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch prayer times');
        }
        return res.json();
      })
      .then(data => {
        // Save raw data for debugging
        setDebugInfo({
          rawData: JSON.stringify(data, null, 2),
          type: typeof data,
          isArray: Array.isArray(data),
          keys: data ? Object.keys(data) : []
        });

        // Try to use the API data if it looks valid
        if (data) {
          if (typeof data === 'object' && data.prayers) {
            setPrayers(data);
            if (data.prayers.Maghrib) {
              startCountdown(data.prayers.Maghrib);
              determineActiveTime(data.prayers);
            }
          } else {
            // Use hardcoded data as fallback
            console.log("Using hardcoded data due to unexpected API format", data);
            setPrayers(hardcodedData);
            startCountdown(hardcodedData.prayers.Maghrib);
            determineActiveTime(hardcodedData.prayers);
          }
        } else {
          throw new Error("Empty response from API");
        }
        setLoading(false);
      })
      .catch(err => {
        console.error("API Error:", err);
        setError(err.message);

        // Always fall back to hardcoded data on errors
        console.log("Using hardcoded data due to API error");
        setPrayers(hardcodedData);
        startCountdown(hardcodedData.prayers.Maghrib);
        determineActiveTime(hardcodedData.prayers);
        setLoading(false);
      });
  };

  const determineActiveTime = (prayerTimes) => {
    if (!prayerTimes) return;

    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    const currentTime = currentHour * 60 + currentMinute;

    let nextPrayer = null;
    let minDiff = Infinity;

    for (const [name, time] of Object.entries(prayerTimes)) {
      if (!time || typeof time !== 'string') continue;

      const parts = time.split(":");
      if (parts.length !== 2) continue;

      const [hour, minute] = parts.map(Number);
      if (isNaN(hour) || isNaN(minute)) continue;

      const prayerTime = hour * 60 + minute;

      // Find the next prayer time
      let diff = prayerTime - currentTime;
      if (diff < 0) {
        diff += 24 * 60; // Add 24 hours if prayer time has passed today
      }

      if (diff < minDiff) {
        minDiff = diff;
        nextPrayer = name;
      }
    }

    setActiveTime(nextPrayer);
  };

  const startCountdown = (iftarTime) => {
    if (!iftarTime) return;

    const interval = setInterval(() => {
      const now = new Date();
      const iftar = new Date();

      const parts = iftarTime.split(":");
      if (parts.length !== 2) {
        setTimeLeft("Invalid time format");
        clearInterval(interval);
        return;
      }

      const [hour, minute] = parts.map(Number);
      if (isNaN(hour) || isNaN(minute)) {
        setTimeLeft("Invalid time values");
        clearInterval(interval);
        return;
      }

      iftar.setHours(hour, minute, 0, 0);

      const diff = iftar - now;
      if (diff <= 0) {
        setTimeLeft("Iftar time!");
        clearInterval(interval);
      } else {
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff / (1000 * 60)) % 60);
        const seconds = Math.floor((diff / 1000) % 60);
        setTimeLeft(`${hours}h ${minutes}m ${seconds}s`);
      }
    }, 1000);

    return () => clearInterval(interval);
  };

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const refreshData = () => {
    fetchPrayerTimes();
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading prayer times...</p>
      </div>
    );
  }

  if (error && !prayers) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={refreshData} className="refresh-btn">Try Again</button>

        {debugInfo && (
          <div className="debug-section">
            <h3>Debug Information</h3>
            <p>Data Type: {debugInfo.type}</p>
            <p>Is Array: {debugInfo.isArray ? "Yes" : "No"}</p>
            <p>Available Keys: {debugInfo.keys.join(", ")}</p>
            <details>
              <summary>Raw Data</summary>
              <pre>{debugInfo.rawData}</pre>
            </details>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className={`app-container ${theme}`}>
      <header>
        <h1>Ramadan Prayer Times</h1>
        <div className="header-actions">
          <button onClick={refreshData} className="refresh-btn">Refresh</button>
          <button onClick={toggleTheme} className="theme-btn">
            {theme === "light" ? "Dark Mode" : "Light Mode"}
          </button>
        </div>
      </header>

      {prayers && (
        <div className="content">
          <div className="location-info">
            <h2>{prayers.city || 'Unknown'}, {prayers.country || 'Location'}</h2>
            <p className="date">{new Date().toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </div>

          <div className="iftar-countdown">
            <h2>Time until Iftar</h2>
            <div className="countdown-display">{timeLeft}</div>
          </div>

          {error && (
            <div className="warning-banner">
              <p>Using backup data due to API error: {error}</p>
            </div>
          )}

          <div className="prayer-times-container">
            <h2>Prayer Times</h2>
            <div className="prayer-times">
              {prayers && prayers.prayers && (
                Object.entries(prayers.prayers).map(([name, time]) => (
                  <div key={name} className={`prayer-time-card ${name === activeTime ? "active" : ""}`}>
                    <div className="prayer-name">{name}</div>
                    <div className="prayer-time">{time}</div>
                  </div>
                ))
              )}
            </div>
          </div>

          {debugInfo && (
            <div className="debug-section">
              <h3>Debug Information</h3>
              <p>Data Type: {debugInfo.type}</p>
              <p>Is Array: {debugInfo.isArray ? "Yes" : "No"}</p>
              <p>Available Keys: {debugInfo.keys.join(", ")}</p>
              <details>
                <summary>Raw Data</summary>
                <pre>{debugInfo.rawData}</pre>
              </details>
            </div>
          )}

          <footer>
            <p>Ramadan Mubarak! ðŸŒ™</p>
          </footer>
        </div>
      )}
    </div>
  );
}

export default App;
