import React, { useEffect, useState } from "react";
import "./App.css"; // We'll create this file for styling

function App() {
  const [prayers, setPrayers] = useState(null);
  const [timeLeft, setTimeLeft] = useState("");
  const [activeTime, setActiveTime] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState("light");

  useEffect(() => {
    fetchPrayerTimes();

    // Apply theme class to body
    document.body.className = theme;

    return () => {
      document.body.className = "body";
    };
  }, [theme]);

  const fetchPrayerTimes = () => {
    setLoading(true);
    setError(null);

    // For testing purposes, use mock data if the API fails
    const useMockData = () => {
      console.log("Using mock data");
      const mockData = {
        city: "Mock City",
        country: "Mock Country",
        prayers: {
          Fajr: "04:30",
          Sunrise: "06:15",
          Dhuhr: "12:30",
          Asr: "15:45",
          Maghrib: "18:45",
          Isha: "20:15"
        }
      };
      processData(mockData);
    };

    // Process data regardless of format
    const processData = (data) => {
      console.log("Processing data:", data);

      try {
        // Attempt to normalize the data structure
        let normalizedData = { prayers: {} };

        // Case 1: Direct object with prayers property
        if (data && typeof data === 'object' && data.prayers && typeof data.prayers === 'object') {
          normalizedData = data;
        } 
        // Case 2: Array format, take first item
        else if (Array.isArray(data) && data.length > 0 && data[0].prayers) {
          normalizedData = data[0];
        }
        // Case 3: Direct prayer times object (no nested prayers property)
        else if (data && typeof data === 'object' && !data.prayers) {
          // Check if it looks like a prayer times object (has typical prayer names)
          const prayerNames = ["Fajr", "Sunrise", "Dhuhr", "Asr", "Maghrib", "Isha"];
          const hasPrayerTimes = prayerNames.some(name => typeof data[name] === 'string');

          if (hasPrayerTimes) {
            normalizedData.prayers = data;
            normalizedData.city = data.city || "Unknown";
            normalizedData.country = data.country || "Location";
          } else {
            throw new Error("Unrecognized data format");
          }
        } else {
          throw new Error("Invalid data format");
        }

        setPrayers(normalizedData);

        if (normalizedData.prayers) {
          determineActiveTime(normalizedData.prayers);

          // Use Maghrib time for Iftar, fallback to a default time if not available
          const iftarTime = normalizedData.prayers.Maghrib || normalizedData.prayers.maghrib || "18:30";
          startCountdown(iftarTime);
        }

        setLoading(false);
      } catch (err) {
        console.error("Data processing error:", err);
        setError(err.message || "Failed to process prayer times data");
        setLoading(false);
      }
    };

    // Attempt to fetch from API
    fetch("http://localhost:5000/api/prayers")
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch prayer times');
        }
        return res.json();
      })
      .then(data => {
        processData(data);
      })
      .catch(err => {
        console.error("Fetch error:", err);
        // If in development mode, use mock data instead of showing error
        if (process.env.NODE_ENV === 'development') {
          useMockData();
        } else {
          setError(err.message || "Failed to fetch prayer times");
          setLoading(false);
        }
      });
  };

  const determineActiveTime = (prayerTimes) => {
    if (!prayerTimes || typeof prayerTimes !== 'object') {
      console.error("Invalid prayer times for active time determination");
      return;
    }

    try {
      const now = new Date();
      const currentHour = now.getHours();
      const currentMinute = now.getMinutes();
      const currentTime = currentHour * 60 + currentMinute;

      let nextPrayer = null;
      let minDiff = Infinity;

      Object.entries(prayerTimes).forEach(([name, time]) => {
        if (typeof time === 'string' && time.includes(':')) {
          const [hour, minute] = time.split(":").map(Number);
          const prayerTime = hour * 60 + minute;

          // Find the next prayer time
          let diff = prayerTime - currentTime;
          if (diff < 0) {
            diff += 24 * 60; // Add 24 hours if prayer time has passed today
          }

          if (diff < minDiff) {
            minDiff = diff;
            nextPrayer = name;
          }
        }
      });

      setActiveTime(nextPrayer || Object.keys(prayerTimes)[0] || "");
    } catch (err) {
      console.error("Error determining active time:", err);
    }
  };

  const startCountdown = (iftarTime) => {
    if (!iftarTime || typeof iftarTime !== 'string' || !iftarTime.includes(':')) {
      setTimeLeft("Iftar time unavailable");
      return () => {};
    }

    const interval = setInterval(() => {
      try {
        const now = new Date();
        const iftar = new Date();
        const [hour, minute] = iftarTime.split(":").map(Number);
        iftar.setHours(hour, minute, 0, 0);

        const diff = iftar - now;
        if (diff <= 0) {
          setTimeLeft("Iftar time!");
          clearInterval(interval);
        } else {
          const hours = Math.floor(diff / (1000 * 60 * 60));
          const minutes = Math.floor((diff / (1000 * 60)) % 60);
          const seconds = Math.floor((diff / 1000) % 60);
          setTimeLeft(`${hours}h ${minutes}m ${seconds}s`);
        }
      } catch (err) {
        console.error("Countdown error:", err);
        setTimeLeft("Countdown error");
        clearInterval(interval);
      }
    }, 1000);

    return () => clearInterval(interval);
  };

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const refreshData = () => {
    fetchPrayerTimes();
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading prayer times...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={refreshData} className="refresh-btn">Try Again</button>
      </div>
    );
  }

  return (
    <div className={`app-container ${theme}`}>
      <header>
        <h1>Ramadan Prayer Times</h1>
        <div className="header-actions">
          <button onClick={refreshData} className="refresh-btn">Refresh</button>
          <button onClick={toggleTheme} className="theme-btn">
            {theme === "light" ? "Dark Mode" : "Light Mode"}
          </button>
        </div>
      </header>

      {prayers && (
        <div className="content">
          <div className="location-info">
            <h2>{prayers.city || 'Unknown'}, {prayers.country || 'Location'}</h2>
            <p className="date">{new Date().toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </div>

          <div className="iftar-countdown">
            <h2>Time until Iftar</h2>
            <div className="countdown-display">{timeLeft}</div>
          </div>

          <div className="prayer-times-container">
            <h2>Prayer Times</h2>
            <div className="prayer-times">
              {prayers && prayers.prayers && Object.keys(prayers.prayers).length > 0 ? (
                Object.entries(prayers.prayers).map(([name, time]) => (
                  <div key={name} className={`prayer-time-card ${name === activeTime ? "active" : ""}`}>
                    <div className="prayer-name">{name}</div>
                    <div className="prayer-time">{time}</div>
                  </div>
                ))
              ) : (
                <div className="no-data">
                  <p>No prayer times available</p>
                  <button onClick={refreshData} className="refresh-btn">Try Again</button>
                </div>
              )}
            </div>
          </div>

          <footer>
            <p>Ramadan Mubarak! ðŸŒ™</p>
          </footer>
        </div>
      )}
    </div>
  );
}

export default App;
