import React, { useEffect, useState, useCallback } from "react";
import "./App.css";
import PrayerForm from "./PrayerForm";

function App() {
  const [prayers, setPrayers] = useState(null);
  const [allCities, setAllCities] = useState([]);
  const [selectedCityId, setSelectedCityId] = useState(null);
  const [timeLeft, setTimeLeft] = useState("");
  const [activeTime, setActiveTime] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState("light");
  const [showForm, setShowForm] = useState(false);

  // Create fetchCities as a useCallback to avoid dependency issues
  const fetchCities = useCallback(() => {
    setLoading(true);
    setError(null);

    fetch("http://127.0.0.1:58908/api/cities")
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch cities');
        }
        return res.json();
      })
      .then(cities => {
        if (Array.isArray(cities) && cities.length > 0) {
          setAllCities(cities);
          // Set the first city as default if no city is selected
          if (!selectedCityId) {
            setSelectedCityId(cities[0].id);
            fetchPrayerTimesForCity(cities[0].id);
          } else {
            fetchPrayerTimesForCity(selectedCityId);
          }
        } else {
          setLoading(false);
          // In case of empty array, try to fetch all prayers instead
          fetchAllPrayers();
        }
      })
      .catch(err => {
        console.error("API Error:", err);
        setError(err.message);
        // If cities endpoint fails, try the prayers endpoint directly
        fetchAllPrayers();
      });
  }, [selectedCityId]); // Include selectedCityId in dependencies

  // Fetch all prayers (fallback if cities endpoint fails)
  const fetchAllPrayers = useCallback(() => {
    setLoading(true);
    setError(null);

    fetch("http://127.0.0.1:58908/api/prayers")
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch prayer times');
        }
        return res.json();
      })
      .then(data => {
        if (Array.isArray(data) && data.length > 0) {
          const prayerData = data[0];

          const formattedPrayers = {
            city: prayerData.city,
            country: prayerData.country,
            prayers: {
              Fajr: formatTime(prayerData.fajr),
              Dhuhr: formatTime(prayerData.dhuhr),
              Asr: formatTime(prayerData.asr),
              Maghrib: formatTime(prayerData.maghrib),
              Isha: formatTime(prayerData.isha)
            }
          };

          setPrayers(formattedPrayers);
          determineActiveTime(formattedPrayers.prayers);
          startCountdown(formattedPrayers.prayers.Maghrib);
        } else {
          throw new Error("No prayer data available");
        }

        setLoading(false);
      })
      .catch(err => {
        console.error("API Error:", err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Fetch prayer times for a specific city
  const fetchPrayerTimesForCity = useCallback((cityId) => {
    if (!cityId) return;

    setLoading(true);
    setError(null);

    fetch(`http://127.0.0.1:58908/api/prayers/${cityId}`)
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch prayer times for this city');
        }
        return res.json();
      })
      .then(prayerData => {
        if (prayerData) {
          const formattedPrayers = {
            city: prayerData.city,
            country: prayerData.country,
            prayers: {
              Fajr: formatTime(prayerData.fajr),
              Dhuhr: formatTime(prayerData.dhuhr),
              Asr: formatTime(prayerData.asr),
              Maghrib: formatTime(prayerData.maghrib),
              Isha: formatTime(prayerData.isha)
            }
          };

          setPrayers(formattedPrayers);
          determineActiveTime(formattedPrayers.prayers);
          startCountdown(formattedPrayers.prayers.Maghrib);
        } else {
          throw new Error("Invalid data format received");
        }

        setLoading(false);
      })
      .catch(err => {
        console.error("API Error:", err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    fetchCities();

    document.body.className = theme;

    return () => {
      document.body.className = "";
    };
  }, [theme, fetchCities]); // Added fetchCities as a dependency

  // Format time from "HH:MM:SS" to "HH:MM"
  const formatTime = (timeString) => {
    if (!timeString) return "";

    if (timeString.includes(":")) {
      const parts = timeString.split(":");
      if (parts.length >= 2) {
        return `${parts[0]}:${parts[1]}`;
      }
    }

    return timeString;
  };

  const determineActiveTime = (prayerTimes) => {
    if (!prayerTimes) return;

    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    const currentTime = currentHour * 60 + currentMinute;

    let nextPrayer = null;
    let minDiff = Infinity;

    for (const [name, time] of Object.entries(prayerTimes)) {
      if (!time || typeof time !== 'string') continue;

      const parts = time.split(":");
      if (parts.length < 2) continue;

      const [hour, minute] = parts.map(Number);
      if (isNaN(hour) || isNaN(minute)) continue;

      const prayerTime = hour * 60 + minute;

      let diff = prayerTime - currentTime;
      if (diff < 0) {
        diff += 24 * 60;
      }

      if (diff < minDiff) {
        minDiff = diff;
        nextPrayer = name;
      }
    }

    setActiveTime(nextPrayer);
  };

  const startCountdown = (iftarTime) => {
    if (!iftarTime) return;

    const interval = setInterval(() => {
      const now = new Date();
      const iftar = new Date();

      const parts = iftarTime.split(":");
      if (parts.length < 2) {
        setTimeLeft("Invalid time format");
        clearInterval(interval);
        return;
      }

      const [hour, minute] = parts.map(Number);
      if (isNaN(hour) || isNaN(minute)) {
        setTimeLeft("Invalid time values");
        clearInterval(interval);
        return;
      }

      iftar.setHours(hour, minute, 0, 0);

      const diff = iftar - now;
      if (diff <= 0) {
        setTimeLeft("Iftar time!");
        clearInterval(interval);
      } else {
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff / (1000 * 60)) % 60);
        const seconds = Math.floor((diff / 1000) % 60);
        setTimeLeft(`${hours}h ${minutes}m ${seconds}s`);
      }
    }, 1000);

    return () => clearInterval(interval);
  };

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const refreshData = () => {
    fetchCities();
  };

 // Modify the handlePrayerAdded function to ensure proper refresh
const handlePrayerAdded = () => {
  setShowForm(false); // Hide the form

  // Add a small delay to ensure the backend has updated
  setTimeout(() => {
    fetchCities(); // Refresh the cities list
  }, 500);
};
  // Toggle form visibility
  const toggleForm = () => {
    setShowForm(!showForm);
  };

  // Handle city selection change
  const handleCityChange = (e) => {
    const cityId = parseInt(e.target.value);
    setSelectedCityId(cityId);
    fetchPrayerTimesForCity(cityId);
  };

  if (loading && !prayers) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading prayer times...</p>
      </div>
    );
  }

  if (error && !prayers) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={refreshData} className="refresh-btn">Try Again</button>
      </div>
    );
  }

  return (
    <div className={`app-container ${theme}`}>
      <header>
        <h1>Ramadan Prayer Times</h1>
        <div className="header-actions">
          <button onClick={toggleForm} className="form-toggle-btn">
            {showForm ? "Hide Form" : "Add Prayer Times"}
          </button>
          <button onClick={refreshData} className="refresh-btn">Refresh</button>
          <button onClick={toggleTheme} className="theme-btn">
            {theme === "light" ? "Dark Mode" : "Light Mode"}
          </button>
        </div>
      </header>

      {/* Prayer Form */}
      {showForm && (
        <div className="form-section">
          <PrayerForm
            onPrayerAdded={handlePrayerAdded}
            onCancel={() => setShowForm(false)}
          />
        </div>
      )}

      {/* City Selection Dropdown */}
      {allCities.length > 0 && (
        <div className="city-selector">
          <label htmlFor="city-select">Select City: </label>
          <select 
            id="city-select" 
            value={selectedCityId || ""} 
            onChange={handleCityChange}
            className="city-dropdown"
          >
            {allCities.map(city => (
              <option key={city.id} value={city.id}>
                {city.city}, {city.country}
              </option>
            ))}
          </select>
        </div>
      )}

      {prayers && (
        <div className="content">
          <div className="location-info">
            <h2>{prayers.city}, {prayers.country}</h2>
            <p className="date">{new Date().toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </div>

          <div className="iftar-countdown">
            <h2>Time until Iftar</h2>
            <div className="countdown-display">{timeLeft}</div>
          </div>

          <div className="prayer-times-container">
            <h2>Prayer Times</h2>
            <div className="prayer-times">
              {prayers && prayers.prayers && (
                Object.entries(prayers.prayers).map(([name, time]) => (
                  <div key={name} className={`prayer-time-card ${name === activeTime ? "active" : ""}`}>
                    <div className="prayer-name">{name}</div>
                    <div className="prayer-time">{time}</div>
                  </div>
                ))
              )}
            </div>
          </div>

          <footer>
            <p>Ramadan Mubarak! ðŸŒ™</p>
          </footer>
        </div>
      )}
    </div>
  );
}

export default App;
