import React, { useEffect, useState } from "react";
import "./App.css"; // We'll create this file for styling

function App() {
  const [prayers, setPrayers] = useState(null);
  const [timeLeft, setTimeLeft] = useState("");
  const [activeTime, setActiveTime] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState("light");

  useEffect(() => {
    fetchPrayerTimes();

    // Apply theme class to body
    document.body.className = theme;

    return () => {
      document.body.className = "";
    };
  }, [theme]);

  const fetchPrayerTimes = () => {
    setLoading(true);
    fetch("http://localhost:5000/api/prayers")
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch prayer times');
        }
        return res.json();
      })
      .then((data) => {
        setPrayers(data);
        determineActiveTime(data.prayers);
        startCountdown(data.prayers.Maghrib);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  };

  const determineActiveTime = (prayerTimes) => {
    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    const currentTime = currentHour * 60 + currentMinute;

    let nextPrayer = null;
    let minDiff = Infinity;

    Object.entries(prayerTimes).forEach(([name, time]) => {
      const [hour, minute] = time.split(":").map(Number);
      const prayerTime = hour * 60 + minute;

      // Find the next prayer time
      let diff = prayerTime - currentTime;
      if (diff < 0) {
        diff += 24 * 60; // Add 24 hours if prayer time has passed today
      }

      if (diff < minDiff) {
        minDiff = diff;
        nextPrayer = name;
      }
    });

    setActiveTime(nextPrayer);
  };

  const startCountdown = (iftarTime) => {
    const interval = setInterval(() => {
      const now = new Date();
      const iftar = new Date();
      const [hour, minute] = iftarTime.split(":").map(Number);
      iftar.setHours(hour, minute, 0, 0);

      const diff = iftar - now;
      if (diff <= 0) {
        setTimeLeft("Iftar time!");
        clearInterval(interval);
      } else {
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff / (1000 * 60)) % 60);
        const seconds = Math.floor((diff / 1000) % 60);
        setTimeLeft(`${hours}h ${minutes}m ${seconds}s`);
      }
    }, 1000);

    return () => clearInterval(interval);
  };

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const refreshData = () => {
    fetchPrayerTimes();
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading prayer times...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={refreshData} className="refresh-btn">Try Again</button>
      </div>
    );
  }

  return (
    <div className={`app-container ${theme}`}>
      <header>
        <h1>Ramadan Prayer Times</h1>
        <div className="header-actions">
          <button onClick={refreshData} className="refresh-btn">Refresh</button>
          <button onClick={toggleTheme} className="theme-btn">
            {theme === "light" ? "Dark Mode" : "Light Mode"}
          </button>
        </div>
      </header>

      {prayers && (
        <div className="content">
          <div className="location-info">
            <h2>{prayers.city}, {prayers.country}</h2>
            <p className="date">{new Date().toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </div>

          <div className="iftar-countdown">
            <h2>Time until Iftar</h2>
            <div className="countdown-display">{timeLeft}</div>
          </div>

          <div className="prayer-times-container">
            <h2>Prayer Times</h2>
            <div className="prayer-times">
            {prayers && prayers.prayers && Object.entries(prayers.prayers).map(([name, time]) => (
  <div key={name} className={`prayer-time-card ${name === activeTime ? "active" : ""}`}>
    <div className="prayer-name">{name}</div>
    <div className="prayer-time">{time}</div>
  </div>
))}

            </div>
          </div>

          <footer>
            <p>Ramadan Mubarak! ðŸŒ™</p>
          </footer>
        </div>
      )}
    </div>
  );
}

export default App;
